import 'package:nfc_manager/nfc_manager.dart';import 'package:nfc_writer/data/model/record.dart';class NfcData {  // NFC bilgileri  String? identifier;  int? sak;  int? maxTransceiveLength;  // Mifare Ultralight bilgileri  int? type;  // NDEF bilgileri  bool? isWritable;  int? maxSize;  List<NdefRecord>? records;  // Constructor  NfcData({    this.identifier,    this.sak,    this.maxTransceiveLength,    this.type,    this.isWritable,    this.maxSize,    this.records,  });  // fromJson metodu  factory NfcData.fromJson(Map<String, dynamic> json) {    // NFC kart bilgilerinin ayrıştırılması    List<int>? identifier = json['nfca']?['identifier']?.cast<int>();    int? sak = json['nfca']?['sak'];    int? maxTransceiveLength = json['nfca']?['maxTransceiveLength'];    // Mifare Ultralight kart türü    int? type = json['mifareultralight']?['type'];    // NDEF bilgileri    bool? isWritable = json['ndef']?['isWritable'];    int? maxSize = json['ndef']?['maxSize'];    // NDEF mesajının ayrıştırılması    List? records = json['ndef']?['cachedMessage']?['records'];    // NfcData nesnesini döndür    return NfcData(      identifier:String.fromCharCodes(identifier??[]),      sak: sak,      maxTransceiveLength: maxTransceiveLength,      type: type,      isWritable: isWritable,      maxSize: maxSize,      records: null,    );  }  // Kart türünü belirleyen yardımcı fonksiyon  String getCardType() {    switch (type) {      case 1:        return "MIFARE Ultralight";      case 2:        return "MIFARE Ultralight C";      case 3:        return "MIFARE Ultralight EV1";      default:        return "Bilinmeyen Tip";    }  }  // Verileri ekrana bastıran bir fonksiyon  void printDetails() {    print("NFC Kartı ID: $identifier");    print("Sak: $sak");    print("Max Transceive Length: $maxTransceiveLength");    print("Kart Türü: ${getCardType()}");    print("Yazılabilir mi: ${isWritable ?? 'Bilinmiyor'}");    print("Max Boyut: $maxSize bytes");    print("NDEF Mesajı: $records");  }}