import 'package:flutter/foundation.dart';import 'package:flutter/material.dart';import 'package:nfc_manager/nfc_manager.dart';import 'package:nfc_writer/data/model/nfc_data.dart';import 'package:nfc_writer/util/context_extensions.dart';import 'home_screen_mixin.dart';/// HomeScreen ekranıclass HomeScreen extends StatefulWidget {  /// HomeScreen ekranı  const HomeScreen({super.key});  @override  State<HomeScreen> createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> with HomeScreenMixin{  @override  Widget build(BuildContext context) {    return MaterialApp(      home: Scaffold(        appBar: AppBar(title: Text('Firesoft NFC Writer')),        body: screens[currentIndex],        bottomNavigationBar: BottomNavigationBar(          selectedItemColor: context.colorScheme.primary,          items: const <BottomNavigationBarItem>[            BottomNavigationBarItem(              icon: Icon(Icons.nfc_outlined),              label: 'Tarama',            ),            BottomNavigationBarItem(              icon: Icon(Icons.edgesensor_high),              label: 'Yazma',            ),          ],          currentIndex: currentIndex,          onTap: (int index) {            setState(() {              currentIndex = index;            });          },        ),      ),    );  }}