import 'package:flutter/material.dart';extension ContextExtensions on BuildContext {  /// Renk şemaları  ColorScheme get colorScheme => Theme.of(this).colorScheme;  /// Metin temaları  TextTheme get textTheme => Theme.of(this).textTheme;  /// Ekran genişliği  double get width => MediaQuery.of(this).size.width;  /// Ekran yüksekliği  double get height => MediaQuery.of(this).size.height;}